import axios from "axios";
import Highcharts from "highcharts";
import * as d3 from "d3";
export default {
  name: "Charts",
  methods: {
    drawGraph(graphData) {
      const svg = d3.select(this.$refs.graphContainer);

      // 清空现有的 SVG 内容
      svg.selectAll('*').remove();

      // 设置 SVG 尺寸
      const width = 900;
      const height = 800;
      svg.attr('width', width).attr('height', height);

      // 创建一个力导向布局
      const simulation = d3.forceSimulation(graphData.nodes).force('charge', d3.forceManyBody()).force('center', d3.forceCenter(width / 2, height / 2));

      // 创建箭头
      svg.append('defs').selectAll('marker').data(['arrow']) // 定义箭头的名称
      .enter().append('marker').attr('id', d => d).attr('viewBox', '0 -5 10 10').attr('refX', 20) // 控制箭头位置
      .attr('refY', 0).attr('markerWidth', 6).attr('markerHeight', 6).attr('orient', 'auto') // 根据路径自动定向箭头
      .append('path').attr('d', 'M0,-5L10,0L0,5').attr('class', 'arrowhead'); // 自定义箭头的样式

      // 绘制边
      const links = svg.selectAll('.link').data(graphData.edges).enter().append('line').attr('class', 'link').style('stroke', 'deepskyblue'); // 设置线段颜色
      // .attr('marker-end', 'url(#arrow)'); // 引用箭头

      // 设置线段的起点和终点坐标，假设节点的位置信息存储在 'nodes' 数组中
      links.attr('x1', d => getNodeXPosition(d.source)).attr('y1', d => getNodeYPosition(d.source)).attr('x2', d => getNodeXPosition(d.target)).attr('y2', d => getNodeYPosition(d.target));

      // 在线段上标注时间
      links.append('text').attr('class', 'time-label').attr('dy', -5) // 调整时间标签的垂直位置
      .text(d => d.time); // 显示时间值

      // 绘制节点
      const nodes = svg.selectAll('.node').data(graphData.nodes).enter().append('g').attr('class', 'node');
      nodes.append('circle').attr('r', 8).attr('fill', 'orange');

      // 添加节点名称
      nodes.append('text').text(d => d.name).attr('x', 12).attr('y', 4);

      // 添加节点和边的交互
      nodes.call(d3.drag().on('start', dragStarted).on('drag', dragged).on('end', dragEnded));

      // 定义拖动开始、拖动过程和拖动结束的函数
      function dragStarted(d) {
        if (!d3.event.active) simulation.alphaTarget(0.3).restart();
        d.fx = d.x;
        d.fy = d.y;
      }
      function dragged(d) {
        d.fx = d3.event.x;
        d.fy = d3.event.y;
      }
      function dragEnded(d) {
        if (!d3.event.active) simulation.alphaTarget(0);
        d.fx = null;
        d.fy = null;
      }

      // 设置力导向布局的每一帧的动作
      simulation.on('tick', () => {
        links.attr('x1', d => d.source.x).attr('y1', d => d.source.y).attr('x2', d => d.target.x).attr('y2', d => d.target.y);
        nodes.attr('transform', d => `translate(${d.x},${d.y})`);
      });
    }
  },
  mounted() {
    this.drawGraph();
  }
};
